import {useState} from 'react';
import axios from 'axios';

const Form = (props) => {
    const [form, setForm] = useState({});

    const changeHandler = (e) => {
        setForm({...form, [e.target.getAttribute("name")]: e.target.value});
    }

    const dropHandler = (e) => {
      setForm({...form, activity: e.target.value})
    }
    console.log(form);

    function setData() {
        if(start = "")
        axios.post('http://localhost:3006/', form).then(()=>props.onUpdate())

    }

    return (
        <>
            <div className="container">
                <div className="header">
                    <h1>Activity Tracker</h1>
                </div>

                <div className="add-activity">
                    <p>Add new activity:</p>
                    <input name= "start" onChange={changeHandler} type="text" placeholder="Start time" className="start-time"/>
                    <input name= "finish" onChange={changeHandler} type="text" placeholder="Finish time" className="finish-time"/>
                    <input name= "distance" onChange={changeHandler} type="text" placeholder="Distance" className="distance"/>
                    <select onChange={dropHandler} placeholder="Select" className="activity-type">
                        <option selected disabled>Select activity type</option>
                        <option value="Run">Run</option>
                        <option value="Ride">Ride</option>
                    </select>
                    <button onClick={setData} className="save">Save</button>
                </div>
            </div>
        </>
    )

}

export default Form;
