import './RecentActivities.css'
import date from 'date-and-time';
import moment from "moment";

const RecentActivities = (props) => {
    const now = new Date();
    const pattern = date.compile('MMMM DD');

    // function timeDifference(date1,date2) {
    //     let difference = date1.getTime() - date2.getTime();
    //
    //     let hoursDifference = Math.floor(difference/1000/60/60);
    //     difference -= hoursDifference*1000*60*60
    //
    //     let minutesDifference = Math.floor(difference/1000/60);
    //     difference -= minutesDifference*1000*60
    //
    //     let secondsDifference = Math.floor(difference/1000);
    //
    //     console.log('difference = ' +
    //         daysDifference + ' day/s ' +
    //         hoursDifference + ' hour/s ' +
    //         minutesDifference + ' minute/s ' +
    //         secondsDifference + ' second/s ');
    // }
    //
    // timeDifference();
    let durationTime = props.data.map(e => {
        console.log((+e.finish_time - +e.start_time)/1000/60/60)
           // return ((+e.finish_time) - (+e.start_time))/60;

        }
    )
    // console.log(durationTime);

    return (
        <div className="activities-container">
            <div className="activity">
                    {props.data ?[...props.data].reverse().map(e =>
                        <div>
                            <p>{date.format(now, pattern)}</p>
                            <p> {e.activity} </p>
                            <p> {e.distance + ' km'} </p>
                            <p> {moment((+e.finish_time) - (+e.start_time)).format("HH:mm")} </p>
                            <p> {} </p>
                        </div>
                    ) : null}
            </div>
        </div>
    )
}

export default RecentActivities;
