import {useRef, useState} from 'react';
import axios from 'axios';

const Form = (props) => {
    const [form, setForm] = useState({});
    const inputRef = useRef();

    const changeHandler = (e) => {
        setForm({...form, [e.target.getAttribute("name")]: e.target.value});
    }

    const dropHandler = (e) => {
      setForm({...form, activity: e.target.value})
    }
    console.log(form);


    function setData() {

        console.log(form.start);
       if (form.start != null && form.distance != null && form.finish != null) {
           axios.post('http://localhost:3006/', form).then(()=>props.onUpdate())
           inputRef.current.value = "";
       }else {
           alert("Please fill in all fields");
       }

    }

    return (
        <>
            <div className="container">
                <div className="header">
                    <h1>Activity Tracker</h1>
                </div>

                <div className="add-activity">
                    <p>Add new activity:</p>
                    <input
                        ref={inputRef}
                        name = "start"
                        onChange={changeHandler}
                        type="text"
                        placeholder = "Start time"
                        className = "start-time"
                    />
                    <input

                        name = "finish"
                        onChange={changeHandler}
                        type = "text"
                        placeholder = "Finish time"
                        className = "finish-time"
                    />
                    <input
                        ref={inputRef}
                        name = "distance"
                        onChange = {changeHandler}
                        type = "text"
                        placeholder = "Distance"
                        className = "distance"
                    />
                    <select onChange={dropHandler} placeholder="Select" className="activity-type">
                        <option selected disabled>Select activity type</option>
                        <option value="Run">Run</option>
                        <option value="Ride">Ride</option>
                    </select>
                    <button onClick={setData} className="save">Save</button>
                </div>

            </div>
        </>
    )

}

export default Form;
