import './Records.css';
import date from "date-and-time";
import axios from "axios";
import {useEffect, useState} from "react";

const Records = () => {

    const [data, setData] = useState(null)

    useEffect(() => {
        axios.get('http://localhost:3006/records')
            .then((response) =>
                setData(response.data))
    }, [])

    const now = new Date();
    const pattern = date.compile('MMM DD');
    return (
        <div className="records-container">
            <p style={{"text-align": "center"}}>Longest ride:</p>
            <div className="records-data">
                {data ? data.map(e =>
                    <div>
                        <p>{date.format(now, pattern)}</p>
                        <p> {e.start_time} </p>
                        <p> {e.finish_time} </p>
                        <p> {e.distance + 'km'} </p>
                        <p> {e.activity} </p>
                    </div>
                ) : null}
            </div>
            {/*<p style={{"text-align": "center"}}>Longest run:</p>*/}
            {/*<div className="records-data">*/}
            {/*<p>{date.format(now, pattern)}</p>*/}
            {/*<p>25 km</p>*/}
            {/*<p> 1h 45m</p>*/}
            {/*</div>*/}
        </div>
    )
}

export default Records;
