import './Records.css';
import date from "date-and-time";
import {useEffect, useState} from "react";

const Records = (props) => {
    console.log(props.data.durationRide);

let a = props.data.durationRide;

    let durationTime = a.map(e => {

            let difference = +e.finish_time - +e.start_time; // /360000

            let hours = Math.floor(difference / 1000 / 60 / 60);
            difference -= hours * 1000 * 60 * 60;

            let minutes = Math.floor(difference / 1000 / 60);
            difference -= minutes * 1000 * 60;

            return [hours + ' h ' + minutes + ' min '];

        }
    )
    console.log(durationTime)

    const now = new Date();
    const pattern = date.compile('MMM DD');
    return (
        <div className="records-container">
            <p style={{"text-align": "center"}}>Longest ride:</p>
            <div className="records-data">
                <p>{date.format(now, pattern)}</p>
                <p>{props.data.distanceRide ? props.data.distanceRide[0].distance : null}</p>
                {}
            </div>
            <p style={{"text-align": "center"}}>Longest run:</p>
            <div className="records-data">
                <p>{date.format(now, pattern)}</p>
                <p>{props.data.distanceRun ? props.data.distanceRun[0].distance : null}</p>
                {/*<p> 1h 45m</p>*/}
            </div>
        </div>
    )
}

export default Records;
